mapper.select:
select name, password
from employee
where
    name = #{userName}
and
    password = #{password}

mapper.update:
update caldav_calendar_info
set calendar_name = #{calendarName}
  , calendar_tag = #{cTag}
  , update_dt = now()
where
    calendar_id = #{calendarId};

update.foreach:
update caldav_event_info
set event_name = #{item.eventName}
, start_dt = #{item.startTime}
, end_dt = #{item.endTime}
, event_tag = #{item.eTag}
, update_user = #{item.updator}
, update_dt = #{item.updateTime}
where event_id = #{item.eventId}
and calendar_id = #{calendarId};

mapper.insert:
insert into caldav_event_info
(
event_id
, calendar_id
, event_name
, start_dt
, end_dt
, event_tag
, wid
, create_user
, update_user
, create_dt
, update_dt
) values

    (
    #{item.eventId}
    , #{calendarId}
    , #{item.eventName}
    , #{item.startTime}
    , #{item.endTime}
    , #{item.eTag}
    , #{item.wid}
    , #{item.creator}
    , #{item.updator}
    , #{item.createTime}
    , #{item.updateTime}
    )
